        private static int SolvePuzzle(string input)
        {
            var result = 0;
            var charArray = input.ToCharArray();

            foreach (char chr in charArray)
            {
                if (chr.Equals('('))
                {
                    result += 1;
                }
                if (chr.Equals(')'))
                {
                    result -= 1;
                }
            }

            return result;
        }
